import React from "react";
import {Link, NavLink} from "react-router-dom"
import { useSelector, useDispatch } from "react-redux"
import { checkIsAuth, logout } from '../redux/features/auth/authSlice'
import {toast} from 'react-toastify'

export const Navbar = () => {
    const isAuth = useSelector(checkIsAuth)
    const dispatch = useDispatch()

    const activeStyles = {color: 'LightSteelBlue'}

    const logoutHandler = () => {
        dispatch(logout())
        window.localStorage.removeItem('token')
        toast('Вы вышли из системы')
    }

    return (
        <div className='flex py-4 justify-between items-center'>
        <span className='flex justify-center items-center w-6 h-6 bg-gray-600 text-xs text-white rounded-sm'>
            E
        </span>
        {isAuth && (
        <ul className='flex gap-8'>
            <li>
                <NavLink to={'/'} href = '/' className='text-xs text-gray-400 hover:text-white' style={({isActive})=>isActive? activeStyles : undefined}> Главная</NavLink>
            </li>
            {/* <li>
                <NavLink to={'/login'}href = '/' style={({isActive})=>isActive? activeStyles : undefined}> Войти</NavLink>
            </li> */}
            {/* <li>
                <NavLink to={'/register'} href = '/' style={({isActive})=>isActive? activeStyles : undefined}> Зарегестрироваться</NavLink>
            </li> */}
            {/* <li>
                <NavLink to={'/post'} href = '/' className='text-xs text-gray-400 hover:text-white' style={({isActive})=>isActive? activeStyles : undefined}> Все публикации</NavLink>
            </li>
            <li>
                <NavLink to={'/posts'} href = '/'  className='text-xs text-gray-400 hover:text-white'style={({isActive})=>isActive? activeStyles : undefined}> Мои публикации</NavLink>
            </li> */}
            <li>
                <NavLink to={'/new'} href = '/' className='text-xs text-gray-400 hover:text-white'style={({isActive})=>isActive? activeStyles : undefined}> Создать публикацию </NavLink>
            </li>
            <li>
                <NavLink href = '/' className='text-xs text-gray-400 hover:text-white'style={({isActive})=>isActive? activeStyles : undefined}> Пиу </NavLink>
            </li>
        </ul>
        )}
        
        <div className='flex justify-center items-center bg-gray-600 text-xs text-white rounded-sm px-4 py-2'>
            {isAuth?(<button onClick={logoutHandler}>Выйти</button>):(<Link to={'/login'}>Войти</Link>)}
        </div>
    </div>
    )
}
