import React from "react";
import { AiFillEye, AiOutlineMessage } from 'react-icons/ai'
import { Moment } from 'react-moment'
import moment from 'moment';

export const PostItem = ({post}) => {
    if (!post) {
        return (
            <div className='text-xl text-center text-white py-10'>
                Нет никаких публикаций
            </div>
        )
    }
    return (
        <div className='flex flex-col basis-1/4 flex-grow'>
            <div>
                className = {
                    post.imgurl
                }
            </div>
            <div className='flex justify-between items-center pt-2'>
                <div className='text-xs text-white opacity-50'>{post.usename}</div>
                <div className='text-xs text-white opacity-50'>
                    {moment(post.createdAt).format("MM/DD/YYYY")}
                </div>
            </div>
            <div className='text-white text-xl'>{post.title}</div>
            <p className='text-white opacity-60 text-xs pt-4 line-clamp-4'>{post.text}</p>

            <div className='flex gap-3 items-center mt-2'>
                <button className='asdasd'>
                    <AiFillEye /> <span>{post.views}</span>
                </button>
                <button className='flex items-center justify-center gap-2 text-xs text-white opacity-50'>
                    <AiOutlineMessage />{' '}
                    <span>{post.comment?.length || 0}</span>
                </button>
            </div>
        </div>
    )
}
