import React, {useState, useEffect} from "react";
import {Link, useNavigate} from "react-router-dom"
import { useDispatch, useSelector} from "react-redux";
import { loginUser, checkIsAuth } from "../redux/features/auth/authSlice";
import {toast} from "react-toastify"

export const LoginPage = () => {
    const [username,setUserName]=useState('')
    const [password,setPassword]=useState('')
    const { status } = useSelector((state) => state.auth)
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const isAuth = useSelector(checkIsAuth)

    useEffect(()=>{
        if(status) toast(status)
        if(isAuth) navigate('/')    
    }, [status, isAuth, navigate])

    const handleSubmit = () => {
        try{
            dispatch(loginUser({username, password}))
        }catch(error){
            console.log(error)

        }
    }
    return (
        <form
        onSubmit={(e) => e.preventDefault()}
        className='w-1/4 h-60 mx-auto mt-40'
    >
        <h1 className='text-lg text-lime-600 text-center'>Авторизация</h1>
        <label className='text-xs text-stone-900'>
            Имя пользователя:
            <input
                type='text'
                value={username}
                onChange={(e) => setUserName(e.target.value)}
                placeholder='Username'
                className='mt-1 text-black w-full rounded-lg bg-gray-400 border py-1 px-2 text-xs outline-none placeholder:text-gray-700'
            />
        </label>

        <label className='text-xs text-stone-900'>
            Пароль:
            <input
                type='password'
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder='Password'
                className='mt-1 text-black w-full rounded-lg bg-stone-900 border py-1 px-2 text-xs outline-none placeholder:text-gray-700'
            />
        </label>

        <div className='flex gap-8 justify-center mt-4'>
            <button
                type='submit'
                onClick={handleSubmit}
                className='flex justify-center items-center text-xs bg-gray-600 text-white rounded-sm py-2 px-4'
            >
                Войти
            </button>
            <Link
                to='/register'
                className='flex justify-center items-center text-xs text-white'
            >
                Нет аккаунта ?
            </Link>
        </div>
    </form>)
}
