import React from "react";
import { useState } from "react"
import { AiFillEye, AiOutlineMessage, AiFillDelete, AiTwotoneEdit } from 'react-icons/ai'
import { useCallback } from 'react'
import moment from 'moment';
import { Link, useParams } from "react-router-dom";
import axios from '../utils/axios'
import { useEffect } from 'react'
import {useDispatch} from 'react-redux'
import {useSelector} from 'react-redux'


export const PostPage = () => {
    // const post = 'res'
    const [post, setPost] = useState(null)
    const params = useParams()

    const {user} = useSelector((state) => state.auth)

    const fetchPost = useCallback(async () => {
        const { data } = await axios.get(`/posts/${params.id}`)
        setPost(data)
    }, [params.id])
    
    useEffect(() => {
        fetchPost()
    }, [fetchPost])

    if (!post) {
        return (
            <div className='text-xl text-center text-white py-10'>
                Секундочку...
            </div>
        )
    }

    return (        
    <div>
      <div>
        <button className='flex justify-center items-center bg-gray-600 text-xs text-white rounded-sm py-2 px-4'>
            <Link className='flex' to={'/'}>
                Назад
            </Link>
        </button>
      </div>
      <div className='flex gap-10 py-8'>
        <div classname='w-2/3'>
            <div className='flex flex-col basis-1/4 flex-grow'>
                <div
                    className={post?.imgUrl? 'flex rouded-sm h-80': 'flex rounded-sm'}>
                    {post?.imgUrl && (
                        <img src={`http://localhost:3002/${post.imgUrl}`} alt='img' className='object-cover w-full'/>
                    )}
                </div>
            </div>
            <div className='flex justify-between items-center pt-2'>
                <div className='text-xs text-white opacity-50'>{post.usename}</div>
                <div className='text-xs text-white opacity-50'>
                    {moment(post.createdAt).format("MM/DD/YYYY")}
                </div>
            </div>
            <div className='text-white text-xl'>{post.title}</div>
            <p className='text-white opacity-60 text-xs pt-4 line-clamp-4'>{post.text}</p>

            <div className='flex gap-3 items-center mt-2'>
                <div>
                    <button className='flex items-center justify-center gap-2 text-xs text-white opacity-50'>
                        <AiFillEye /> 
                        <span>{post.views}</span>
                    </button>
                    <button className='flex items-center justify-center gap-2 text-xs text-white opacity-50'>
                        <AiOutlineMessage />
                        <span>{post.comment?.length || 0}</span>
                    </button>
                </div>

                {user?._id === post.author && (
                    <div className='flex gap-3 mt-4'>
                        <button className='flex items-center justify-center gap-2 text-white opacity-50'>
                            <Link to={`/${params.id}/edit`}>
                                {/* <AiTwotoneEdit /> */}
                            </Link>
                        </button>
                        <button
                            // onClick={removePostHandler}
                            className='flex items-center justify-center gap-2  text-white opacity-50'
                        >
                            {/* <AiFillDelete /> */}
                        </button>
                    </div>
                )}
            </div>
        </div>
      </div>
    </div>    
    )
}
